from django import formsfrom shop.models import Orderclass BasketAddProductForm(forms.Form):    count = forms.IntegerField(min_value=1, initial=1, label='Количество',                               widget=forms.NumberInput(attrs={'class': 'w-full p-2 rounded-md bg-white text-black border border-orange-500 focus:border-orange-400 focus:ring focus:ring-orange-400 focus:ring-opacity-50'}))    reload = forms.BooleanField(required=False, initial=False, widget=forms.HiddenInput)class OrderForm(forms.ModelForm):    class Meta:        model = Order        fields = (            'buyer_surname',            'buyer_name',            'buyer_middlename',            'comment',            'delivery_address',            'delivery_type'        )