from django import formsfrom django.contrib.auth.forms import UserCreationForm, AuthenticationFormfrom django.contrib.auth.models import Userfrom .models import Clothes, Brand, CategoryProduct, PetType, Promotion, CatalogProductclass ClothesForm(forms.ModelForm):    class Meta:        model = Clothes        fields = ['name', 'description', 'price', 'size', 'color', 'photo', 'is_exists', 'category', 'collection']class BrandsForm(forms.ModelForm):    class Meta:        model = Brand        fields = ['BrandName', 'Img']class CategoryProductForm(forms.ModelForm):    class Meta:        model = CategoryProduct        fields = ['CategoryName', 'PetType']class PetTypeForm(forms.ModelForm):    class Meta:        model = PetType        fields = ['TypeName', 'Description']class PromotionForm(forms.ModelForm):    class Meta:        model = Promotion        fields = ['PromotionName', 'DiscountPercentage', 'StartDate', 'EndDate', 'CatalogProduct']class CatalogProductForm(forms.ModelForm):    class Meta:        model = CatalogProduct        fields = ['ProductName', 'PriceOfProduct', 'DescriptionProduct', 'Img', 'Quantity', 'Brand', 'Category']class RegistrationForm(UserCreationForm):    username = forms.CharField(        label='Логин пользователя',        widget=forms.TextInput(attrs={'class': 'form-control dark-input'}),        min_length=2    )    email = forms.CharField(        label='Электронная почта',        widget=forms.EmailInput(attrs={'class': 'form-control dark-input'}),    )    password1 = forms.CharField(        label='Придумайте пароль',        widget=forms.PasswordInput(attrs={'class': 'form-control dark-input'}),    )    password2 = forms.CharField(        label='Повторите пароль',        widget=forms.PasswordInput(attrs={'class': 'form-control dark-input'}),    )    class Meta:        model = User        fields = ['username', 'email', 'password1', 'password2']class LoginForm(AuthenticationForm):    username = forms.CharField(        label='Логин пользователя',        widget=forms.TextInput(attrs={'class': 'form-control dark-input'}),    )    password = forms.CharField(        label='Введите пароль',        widget=forms.PasswordInput(attrs={'class': 'form-control dark-input'}),    )