from django import formsfrom .models import Clothes, Brand, CategoryProduct, PetType, Promotion, CatalogProductclass ClothesForm(forms.ModelForm):    class Meta:        model = Clothes        fields = ['name', 'description', 'price', 'size', 'color', 'photo', 'is_exists', 'category', 'collection']class BrandsForm(forms.ModelForm):    class Meta:        model = Brand        fields = ['BrandName', 'Img']class CategoryProductForm(forms.ModelForm):    class Meta:        model = CategoryProduct        fields = ['CategoryName', 'PetType']class PetTypeForm(forms.ModelForm):    class Meta:        model = PetType        fields = ['TypeName', 'Description']class PromotionForm(forms.ModelForm):    class Meta:        model = Promotion        fields = ['PromotionName', 'DiscountPercentage', 'StartDate', 'EndDate', 'CatalogProduct']class CatalogProductForm(forms.ModelForm):    class Meta:        model = CatalogProduct        fields = ['ProductName', 'PriceOfProduct', 'DescriptionProduct', 'Img', 'Quantity', 'Brand', 'Category']